import { Component } from '@angular/core';
import { DocumentTypeModel } from 'src/app/Models/DocumentType/DocumentTypeModel';
import { GetRolModel } from 'src/app/Models/Rol/RolModel';
import { CreateUserModel } from 'src/app/Models/Users/CreateUserModel';
import { UserServiceService } from 'src/app/services/Users/user-service.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent {
  user: CreateUserModel = {
    name: '',
    image: "", // Asigna la imagen base64 obtenida a la propiedad "image"
    lastName: '',
    secondLastName: '',
    dateOfBirth: new Date(),
    numberPhone: '',
    document: '',
    email: '',
    password: '',
    documentTypeId: 0,
    addressId: 0,
    rolId: 0,
  }
  roles: GetRolModel[] =
    [
      {
        id: 1,
        name: 'SuperAdmin'
      },
      {
        id: 1,
        name: 'Usuario de la pagina'
      }
    ]
    documentTypes: DocumentTypeModel[] =
    [
      {
        id: 1,
        name: 'SuperAdmin'
      },
      {
        id: 1,
        name: 'Usuario de la pagina'
      }
    ]
    imagePreview:string = '../../../'
  constructor(
    private userService: UserServiceService
  ) { }

  OnCreateUser(){

  }
  onImageUpload(event: any) {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e: any) => {
        this.user.image = e.target.result;
      };
      reader.readAsDataURL(file);
    }
  }
  createUser() {
    this.userService.create(this.user)
      .subscribe(reponse => {
        console.log(reponse)
      });
  }
}
