import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { DocumentTypeModel } from 'src/app/Models/DocumentType/DocumentTypeModel';
import { GetRolModel } from 'src/app/Models/Rol/RolModel';
import { CreateUserModel } from 'src/app/Models/Users/CreateUserModel';
import { UserServiceService } from 'src/app/services/Users/user-service.service';


@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent {
    registroForm: FormGroup;
    imagenSeleccionada: File = new File([],'');
    imagenBase64: string = '';
    constructor
    (
      private formBuilder: FormBuilder,
      private userService : UserServiceService
    ) {

      this.registroForm = this.formBuilder.group({
        name: ['', Validators.required],
        image: ['', Validators.required],
        lastName: ['', Validators.required],
        secondLastName:['', Validators.required],
        dateOfBirth: ['', Validators.required],
        numberPhone: ['', Validators.required],
        document: ['', Validators.required],
        email: ['', Validators.required],
        password: ['', Validators.required],
        documentTypeId: ['',Validators.required],
        addressId: [''],
        rolId: ['', Validators.required]
      });
    }

    uploadFile(file:Blob){
      const dto = new FormData()
      dto.append('image',file);
    }

    onUploadFile(event:Event){
      const elemento = event.target as HTMLInputElement;
      const file = elemento.files?.item(0);
      if(file){
        this.imagenSeleccionada = file;
      }
    }

    createUser() {
      if (this.registroForm.invalid) {
        // Si el formulario no es válido, puedes mostrar un mensaje de error o realizar alguna acción adicional
        return;
      }

      // Crear un nuevo objeto de usuario para enviar al backend
      const usuarioEnviar: CreateUserModel = {
        name: this.registroForm.value.name,
        image: this.imagenBase64,
        lastName: this.registroForm.value.lastName,
        secondLastName: this.registroForm.value.secondLastName,
        dateOfBirth: this.registroForm.value.dateOfBirth,
        numberPhone: this.registroForm.value.numberPhone,
        document: this.registroForm.value.document,
        email: this.registroForm.value.email,
        password: this.registroForm.value.password,
        documentTypeId: this.registroForm.value.documentTypeId,
        addressId: this.registroForm.value.addressId,
        rolId: this.registroForm.value.rolId
      };

      // Aquí puedes realizar las operaciones necesarias para enviar el objeto 'usuarioEnviar' al backend a través de una solicitud HTTP
      // Asegúrate de manejar adecuadamente el envío de la imagen y otros datos según las necesidades de tu backend

      console.log(usuarioEnviar);
    }

    onFileChange(event: any) {
      this.imagenSeleccionada = event.target.files[0];
      if (this.imagenSeleccionada) {
        const reader = new FileReader();
        reader.onload = (e: any) => {
          this.imagenBase64 = e.target.result;
        };
        reader.readAsDataURL(this.imagenSeleccionada);
      } else {
        this.imagenBase64 = '';
      }
    }
  }











<div class="container">
  <div class="logo">
    <img src="../../../../assets/LogoSinFondo.png" alt="Logo de la aplicación">
  </div>
  <form [formGroup]="registroForm" (ngSubmit)="createUser()">
    <div class="form-row">
      <div class="form-group col">
        <label for="name">Nombre:</label>
        <input type="text" id="name" formControlName="name" class="form-control" />
      </div>

      <div class="form-group col">
        <label for="image">Imagen:</label>
        <input type="file" id="image" (change)="onFileChange($event)" class="form-control-file" />
      </div>

      <div class="form-group col" *ngIf="imagenBase64">
        <img [src]="imagenBase64" alt="Imagen cargada" width="150" height="150" class="img-fluid" />
      </div>
    </div>

    <div class="form-row">
      <div class="form-group col">
        <label for="lastName">Apellido Paterno:</label>
        <input type="text" id="lastName" formControlName="lastName" class="form-control" />
      </div>

      <div class="form-group col">
        <label for="secondLastName">Apellido Materno:</label>
        <input type="text" id="secondLastName" formControlName="secondLastName" class="form-control" />
      </div>

      <div class="form-group col">
        <label for="dateOfBirth">Fecha de Nacimiento:</label>
        <input type="date" id="dateOfBirth" formControlName="dateOfBirth" class="form-control" />
      </div>
    </div>

    <div class="form-row">
      <div class="form-group col">
        <label for="numberPhone">Número de Teléfono:</label>
        <input type="text" id="numberPhone" formControlName="numberPhone" class="form-control" />
      </div>

      <div class="form-group col">
        <label for="document">Documento:</label>
        <input type="text" id="document" formControlName="document" class="form-control" />
      </div>

      <div class="form-group col">
        <label for="email">Correo Electrónico:</label>
        <input type="email" id="email" formControlName="email" class="form-control" />
      </div>
    </div>

    <div class="form-row">
      <div class="form-group col">
        <label for="password">Contraseña:</label>
        <input type="password" id="password" formControlName="password" class="form-control" />
      </div>

      <div class="form-group col">
        <label for="documentTypeId">Tipo de Documento:</label>
        <select id="documentTypeId" formControlName="documentTypeId" class="form-control">
          <option value="" disabled selected>Seleccionar</option>
          <option value="1">Documento 1</option>
          <option value="2">Documento 2</option>
          <!-- Agrega más opciones según sea necesario -->
        </select>
      </div>

      <div class="form-group col">
        <label for="addressId">Dirección:</label>
        <input type="text" id="addressId" formControlName="addressId" class="form-control" />
      </div>
    </div>

    <div class="form-row">
      <div class="form-group col">
        <label for="rolId">Rol:</label>
        <select id="rolId" formControlName="rolId" class="form-control">
          <option value="" disabled selected>Seleccionar</option>
          <option value="1">Rol 1</option>
          <option value="2">Rol 2</option>
          <!-- Agrega más opciones según sea necesario -->
        </select>
      </div>
    </div>

    <div class="form-group">
      <button type="submit" [disabled]="registroForm.invalid || !imagenBase64" class="btn btn-primary">Guardar</button>
    </div>
  </form>
</div>
