import { Component } from '@angular/core';
import { CredentialModel } from 'src/app/Models/Authenticated/CredentilsModel';
import { GetUserModel } from 'src/app/Models/Users/GetUserModel';
import { AutorizationService } from 'src/app/services/Authorization/autorization.service';
import { UserServiceService } from 'src/app/services/Users/user-service.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {
  userCredentials: CredentialModel = { email: '', password: '' }
  private porfile : GetUserModel | null = null;

 constructor(
  private autorizationService: AutorizationService,
  private userService: UserServiceService
 ){}

 login() {
  this.userCredentials.email = 'luisandres@gmail.com';
  this.userCredentials.password = 'Hola123*'
  this.autorizationService.login(this.userCredentials)
    .subscribe(response => {
      console.log(response);
    })
}

getProfile(){
  this.autorizationService.getProfile()
  .subscribe(response =>{
    this.porfile = response.data;
  });
}

onLogin(){

}
}
<div class="container">
  <div class="logo-container">
    <img src="../../../../assets/LogoSinFondo.png" alt="Logo de la aplicación" class="logo-image">
  </div>
  <form (ngSubmit)="onLogin()" #loginForm="ngForm">
    <div class="form-group">
      <label for="email">Correo Electrónico</label>
      <input type="email" id="email" name="email" class="form-control" [(ngModel)]="userCredentials.email" required>
      <p class="error-message" *ngIf="loginForm.controls['email'].invalid && loginForm.controls['email'].touched">
        El correo electrónico es requerido.
      </p>
    </div>
    <div class="form-group">
      <label for="password">Contraseña</label>
      <input type="password" id="password" name="password" class="form-control" [(ngModel)]="userCredentials.password"
        required>
      <p class="error-message" *ngIf="loginForm.controls['password'].invalid && loginForm.controls['password'].touched">
        La contraseña es requerida.
      </p>
    </div>

    <div class="form-group">
      <div class="text-center">
        <button [disabled]="loginForm.invalid" type="submit" class="btn btn-primary">Iniciar sesión</button>
      </div>
    </div>

    <div class="form-group">
      <div class="text-center">
        <a routerLink="/register" class="register-link">Regístrate</a> |
        <a href="#" class="forgot-password-link">Recuperar contraseña</a>
      </div>
    </div>
  </form>
</div>
