import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { GetUserModel } from 'src/app/Models/Users/GetUserModel';


@Injectable({
  providedIn: 'root'
})
export class TokenService {
  private logged: boolean = false;
  constructor(

  ) { }

  saveToken(token: string) {
    localStorage.setItem('Token', token);
    this.logged = true;
  }
  saveUserToken(user: GetUserModel) {
    const userJSON = JSON.stringify(user);
    localStorage.setItem('UserSesion', userJSON);
    const logged = JSON.stringify(this.logged);
    localStorage.setItem('Logueado', logged)
  }

  getToken() {
    const token = localStorage.getItem('Token')
    return token;
  }

}
