import { Component } from '@angular/core';

@Component({
  selector: 'app-jutification-modal',
  templateUrl: './jutification-modal.component.html',
  styleUrls: ['./jutification-modal.component.scss']
})
export class JutificationModalComponent {

}
<div id="modal" class="modal">
  <h1>Justificacion Modal</h1>
  <div class="modal-content">
    <span class="close">&times;</span>
    <h2>Mensaje</h2>
    <p>¡Este es un mensaje en el modal!</p>
  </div>
</div>
