import { ChangeDetectorRef, Component, NgModule, ViewChild } from '@angular/core';
import { GetUserModel } from 'src/app/Models/Users/GetUserModel';
import { TokenService } from 'src/app/services/Tokens/token.service';
import { faAngleUp, faAngleDown, faBars, faXmark} from '@fortawesome/free-solid-svg-icons';
import { library } from '@fortawesome/fontawesome-svg-core';
import { Router } from '@angular/router';
import { MatSidenav } from '@angular/material/sidenav';
import {BreakpointObserver} from '@angular/cdk/layout'
import { faRightFromBracket,faUsers,faPhone, faUserSecret} from '@fortawesome/free-solid-svg-icons';
import { MenuMobileComponent } from '../../modals/menu-mobile/menu-mobile.component';


@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss'],
})

export class HeaderComponent {
  loggedIn : boolean = this.tokenService.getToken() != null;
  faAngleDown = faAngleDown;
  faAngleUp = faAngleUp;
  faBars = faBars;
  faXmark = faXmark;
  faRightFromBracket = faRightFromBracket;
  faUsers = faUsers;
  faPhone = faPhone;
  faUserSecret = faUserSecret;
  user : GetUserModel | null = null;
  isMenuOpen: boolean = false;
  constructor(
   private tokenService:TokenService,
   private router : Router,
   private observer :BreakpointObserver,
   private cd:ChangeDetectorRef,


  ){
   this.getUserLogged()
  }
  toggleMenu(){
    this.isMenuOpen = !this.isMenuOpen;
     console.log(this.isMenuOpen)
  }
  // ngAfterViewInit(){
  //   this.observer.observe(['(max-width: 640px)']).subscribe((res:any )=>{
  //     if(res.matches){
  //       this.sidenav.mode ='over';
  //       this.sidenav.close();
  //     }else{
  //       this.sidenav.mode ='side';
  //       this.sidenav.open();
  //     }
  //   })
  //   this.cd.detectChanges();
  // }

  toggleMenuMobile() {
    this.isMenuOpen = !this.isMenuOpen;
    this.router.navigateByUrl('/menu-moblie')
  }

  navigateTo(route: string) {
    this.router.navigateByUrl(route);
  }
  ngOnInit(): void {
    this.getUserLogged()
  }

  signOff(){
    this.loggedIn = false;
    localStorage.removeItem('UserSesion')
    localStorage.removeItem('Token')
  }
  getUserLogged(){
    if(this.loggedIn){
      const userJSON = localStorage.getItem('UserSesion');
      if (userJSON) {
         this.user = JSON.parse(userJSON);
         console.log("Usuario en sesion: ", this.user)
      }
    }
  }
}
<head>
  <link rel="preconnect" href="https://fonts.googleapis.com">
  <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
  <link href="https://fonts.googleapis.com/css2?family=Quicksand:wght@300;500;700&display=swap" rel="stylesheet">
</head>

<body>
  <nav class="menu-desktop">
    <div (click)="toggleMenu()">
      <fa-icon class="menu" [icon]="faBars"></fa-icon>
    </div>

    <div class="navbar-left">
      <app-menu-mobile [handleClose]="toggleMenu" [showMenu]="isMenuOpen"></app-menu-mobile>
      <img class="logo" src="../../../../assets/LogoSinFondo.png" alt="logo">
      <ul>
        <li>
          <a href="/">Usuarios</a>
        </li>
        <li>
          <a href="/">Roles</a>
        </li>
        <li>
          <a href="/">Cantactenos</a>
        </li>
      </ul>
    </div>
    <cdk-accordion>
      <cdk-accordion-item #accordionMenu="cdkAccordionItem">
        <div class="navbar-right">
          <ul>
            <li (click)="accordionMenu.toggle()" class="navbar-email">
              luisandres@gmail.com
              <fa-icon class="icono" [icon]="accordionMenu.expanded ? faAngleUp : faAngleDown"></fa-icon>
            </li>
            <li *ngIf="!isMenuOpen" class="navbar-image-user">
              <img  src="../../../../assets/usuario.jpg" alt="imagen usuario">
              <div></div>
            </li>
          </ul>
        </div>
        <app-menu-desktop [style.display]="accordionMenu.expanded ? '':'none'"></app-menu-desktop>
      </cdk-accordion-item>
    </cdk-accordion>
  </nav>
</body>
