import { Component } from '@angular/core';
import { FormBuilder, FormControl, Validators,FormGroup } from '@angular/forms';
import { CreateUserModel } from 'src/app/Models/Users/CreateUserModel';
import { UserServiceService } from 'src/app/services/Users/user-service.service';


@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent {

    imagenSeleccionada: File = new File([],'');
    imagenBase64: string = '';

    form = new FormGroup({
      name: new FormControl('name'),
      image : new FormControl(),
      lastName: new FormControl('last name'),
      secondLastName: new FormControl('Secund last name'),
      dateOfBirth: new FormControl(),
      documentTypeId: new FormControl(),
      document: new FormControl(),
      numberPhone: new FormControl(),
      email: new FormControl('luisandres@gmail.com', Validators.required),
      addressId: new FormControl(),
      rolId: new FormControl()
    });


    constructor
    (
      private formBuilder: FormBuilder,
      private userService : UserServiceService
    ) {

    }

    RegisterUser(event: Event){
      console.log(this.form.value)
    }

    uploadFile(file:Blob){
      const dto = new FormData()
      dto.append('image',file);
    }

    onUploadFile(event:Event){
      const elemento = event.target as HTMLInputElement;
      const file = elemento.files?.item(0);
      if(file){
        this.imagenSeleccionada = file;
      }
    }

    get name(){
      return this.form.get('name');
    }

    convertFromBase64ToBytes(base64String: string): Uint8Array {
      const raw = atob(base64String.replace(/^data:image\/\w+;base64,/, ''));
      const length = raw.length;
      const bytes = new Uint8Array(length);

      for (let i = 0; i < length; i++) {
        bytes[i] = raw.charCodeAt(i);
      }
      return bytes;
    }
    onFileChange(event: any) {
      this.imagenSeleccionada = event.target.files[0];
      if (this.imagenSeleccionada) {
        const reader = new FileReader();
        reader.onload = (e: any) => {
          this.imagenBase64 = e.target.result;
          console.log("Base64",this.imagenBase64)
        };
        reader.readAsDataURL(this.imagenSeleccionada);
      }
    }
  }











<head>
  <link rel="preconnect" href="https://fonts.googleapis.com">
  <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
  <link href="https://fonts.googleapis.com/css2?family=Quicksand:wght@300;500;700&display=swap" rel="stylesheet">
</head>
<body>
  <div class="register">
    <img class="logo" src="../../../../assets/LogoSinFondo.png" alt="logo">
    <h1 class="title">Registrar usuarios</h1>

    <div class="form-container">

      <form [formGroup]="form" (ngSubmit)="RegisterUser($event)" class="form">

        <div class="row">
          <label for="name">Nombre</label>
          <input type="text" id="name" formControlName="name" class="input input-name">
          <label for="last-name">Primer apellido</label>
          <input type="text" id="last-name" formControlName="lastName"  class="input input-last-name">
          <label for="Secund-last-name">Segundo Apellido</label>
          <input type="text" id="Secund-last-name" formControlName="secondLastName"  class="input input-secund-last-name">
        </div>

        <div class="row">
          <label for="dateOfBirth">Fecha de nacimiento</label>
          <input type="date" id="dateOfBirth" formControlName="dateOfBirth" class="input input-dateOfBirth">
          <label for="documentTypeId">Tipo de documento</label>
          <select formControlName="documentTypeId" name="documentTypeId" id="documentTypeId">
            <option value="">Selecciona un valor</option>
            <option value="1">Cedula de ciudadania</option>
            <option value="2">Pasaporte</option>
            <option value="3">Cedula extrangera</option>
            <option value="4">Visa</option>
            <!-- Agrega más opciones según los valores que recibas del backend -->
          </select>
          <label for="document">Documento</label>
          <input type="text" id="document" formControlName="document"class="input input-secund-last-name">
        </div>

        <div class="row">
          <label for="email">Email</label>
          <input type="email" id="email" formControlName="email" class="input input-email">
          <label for="numberPhone">Número de telefono</label>
          <input type="tel" id="numberPhone" formControlName="numberPhone" class="input input-numberphone">
          <label for="addressId">Dirección</label>
          <input type="text" id="addressId" formControlName="addressId"class="input input-secund-address">
        </div>

        <div class="row">
          <label for="roles">Rol</label>
          <select formControlName="rolId" name="roles" id="roles">
            <option value="">Selecciona un valor</option>
            <option value="1">Super Administrador</option>
            <option value="2">Cliente</option>
            <option value="3">Supervisor</option>
            <!-- Agrega más opciones según los valores que recibas del backend -->
          </select>
        </div>
        <div class="row">
          <label for="image">Imagen</label>
          <input id="image" type="file">
          <div>

          </div>
        </div>
        <app-button-aceptar>Register</app-button-aceptar>
      </form>
    </div>
  </div>
</body>
