import { getCurrencySymbol } from '@angular/common';
import { Component, EventEmitter, Output } from '@angular/core';
import { plainToClass } from 'class-transformer';
import { CredentialModel } from 'src/app/Models/Authenticated/CredentilsModel';
import { GetUserModel } from 'src/app/Models/Users/GetUserModel';
import { AutorizationService } from 'src/app/services/Authorization/autorization.service';
import { UserServiceService } from 'src/app/services/Users/user-service.service';


@Component({
  selector: 'app-login-component',
  templateUrl: './login-component.component.html',
  styleUrls: ['./login-component.component.scss']
})
export class LoginComponentComponent {
  private credentials: CredentialModel = { Email: '', Password: '' }
  private token = '';
  private porfile : GetUserModel =
  {
    userId:0,
    name: '',
    image: "", // Asigna la imagen base64 obtenida a la propiedad "image"
    lastName: '',
    secondLastName: '',
    dateOfBirth: new Date(),
    numberPhone: '',
    document: '',
    email: '',
    documentTypeId: 0,
    addressId: 0,
    rolId: 0,
    edad:0
  }

  constructor(
    private autorizationService: AutorizationService,
    private userService: UserServiceService
  ) { }

  login() {
    this.credentials.Email = 'luisandres@gmail.com';
    this.credentials.Password = 'Hola123*'
    this.autorizationService.login(this.credentials)
      .subscribe(response => {
        this.token = response.data.toString();
        console.log(response);
      })
  }

  getProfile(){
    this.autorizationService.getProfile(this.token)
    .subscribe(response =>{
      this.porfile = response.data;
    });
  }

  getToken(){
    return this.token;
  }
  getPorfile(){
    return this.porfile;
  }
}






<button (click)="login()">Login</button>
<button (click)="getProfile()">Profile</button>
